import React, { useState, useEffect } from 'react';
import './App.css';
import Post from './Post';
import { collectPosts } from './firebase'
import { doc, onSnapshot } from 'firebase/firestore';
import { Modal } from '@material-ui/core';

function App() {
  const [posts, setPosts] = useState([]);

  // runs piece of code on specific condition
  useEffect(() => {
    onSnapshot(collectPosts, (snapshot) => {
      // when posts changes this code runs
      setPosts(snapshot.docs.map(doc=> ({
        id: doc.id,
        post: doc.data()
      })));

    })
  }, []);

  return (
    <div className="app">
      <Modal
        open={open}
        onClode={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describeby="simple-modal-description"
        >
          {body}
        </Modal>

      <div className="app_header">
        <img
          className="app_header_image"
          // get logo
          // src="../Logo/logo.jpeg"
          src="https://images.squarespace-cdn.com/content/v1/575a6067b654f9b902f452f4/1552683653140-0UUVQSSUEWVC73AWAEQG/300Logo.png" 
          alt=""
        />
        <h1>Welcome to Lens Cleanse!!</h1>
      </div> 

      {
        posts.map(({id, post}) => (
          <Post key={id} username={post.username} caption={post.caption} imageUrl={post.imageUrl}/>
        ))
      } 

    </div>
  );
}

export default App;
