import { Button } from '@material-ui/core';
import React, { useState } from 'react';
import { storage, collectPosts } from './firebase';
import { ref, getDownloadURL } from 'firebase/storage';
import { serverTimestamp, FieldValue } from 'firebase/firestore';

function ImageUpload({username}) {
    const [image, setImage] = useState(null);
    const [progress, setProgress] = useState(0);
    const [caption, setCaption] = useState('');

    const handleChange = (e) => {
        if (e.target.files[0]) {
            setImage(e.target.files[0]);
        }
    };

    const handleUpload = () => {
        // const uploadTask = ref(storage, `images/${image.name}`).put(image);
        //************** NEEDS TO BE FIXED: put ***************/
        const uploadTask = ref(storage, `images/${image.name}`);
        uploadTask.on(
            "state_changed",
            (snapshot) => {
                // progress function
                const progress = Math.round(
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                );
                setProgress(progress);
            },
            (error) => {
                console.log(error);
                alert(error.message);
            },
            () => {
                // complete function
                getDownloadURL(ref(storage, "images").child(image.name))
                .then(url => {
                    collectPosts.add({
                        timestamp: serverTimestamp(FieldValue),
                        caption: caption,
                        imageUrl: url,
                        username: username
                    });

                    setProgress(0);
                    setCaption("");
                    setImage(null);

                })
            }
        )
    };

    return (
        <div>

            <progress className="imageupload_progress" value={progress} max="100" />
            <input type="text" placeholder="Enter a caption..." onChange={event => setCaption(event.target.value)} value={caption}/>
            <input type="file" onChange={handleChange}/>
            <Button onClick={handleUpload}>
                Upload
            </Button>

        </div>
    )
}

export default ImageUpload
